import React,{Component} from 'react';
import './fonts/DMMono-Regular.ttf';
import { FaGithub, FaEnvelope, FaLinkedin, FaSeedling, FaArrowDown,FaPlus } from 'react-icons/fa';
import './App.css';
import Sidebar from './components/sidebars';
import wfirst from './wfirst.jpg';
import gpu from './gpu.jpg';
import graph_caltech from './graph_caltech.jpg';
import results_caltech from './results_caltech.jpg';
import original_caltech from './original_caltech.jpg';

class App extends Component {
	componentDidMount() {
	    window.addEventListener("scroll", this.resizeHeaderOnScroll);
	}
	resizeHeaderOnScroll() {
		const distanceY = window.pageYOffset || document.documentElement.scrollTop,
		shrinkOn = 	20,
		headerEl = document.getElementById("App-header"),
		//nameEl = document.getElementById("name");
		//circleEl = document.getElementById("circle");
		aboutEl = document.getElementById("about");

		if (distanceY > shrinkOn) {
		  headerEl.classList.add("smaller");
		  aboutEl.classList.add("after")
		  //nameEl.classList.add("smaller");
		  //circleEl.classList.add("smaller");
		} else {
		  headerEl.classList.remove("smaller");
		  aboutEl.classList.remove("after")

		  //nameEl.classList.remove("smaller");
		  //circleEl.classList.remove("smaller");
		}
	}

	render() {
		return (
			    <div >
			    	<div class="homepage">
	      				<header id="App-header">
	      					<h2 id="name" > <b> ISHITA KORDE </b> </h2>
	      					<h3 id="name" > <b> computer scienctist <br /> <br /> sustainable living advocate </b> </h3>
	      					<h3 id="name" > <b>  </b> </h3>
	      					<h1 id="circle"> </h1>
		        		</header>

		        		<div class="sidebar-bkgd">
		        			<div class="sidebar-line"> </div>
			       	 		<h3 class="sidebar"> <b> about  </b> </h3>
			       	 		<h3 class="sidebar"> <b> resume  </b> </h3>
		       	 			<h3 class="sidebar"> <b> <FaGithub size="1.4em"/>  </b> </h3>
			       	 		<h3 class="sidebar"> <b> <FaEnvelope size="1.3em"/>  </b> </h3>
			       	 		<h3 class="sidebar"> <b> <FaLinkedin size="1.4em"/>  </b> </h3>
		       	 		</div>

		       	 		<p id= "about">
		       	 			<div class="title"><b> hi, </b></div>
		       	 			<div class="list"> I'm a Computer Science graduate with an interest in developing technology to take care of planet earth. 
		       	 			<br />
		       	 			<br />
		       	 			Throughout my undergrad, I have been involved in projects that use computers to advance science. This intersection, and 
		       	 			specifically, technology with an aim to reduce carbon emissions, is my cup of tea</div>
		       	 			<br />
		       	 			<div class="title"> CURRENTLY...</div>
		       	 			<div class="list"> <FaSeedling size="0.7em" color='#689791'/> Computer Vision Nanodegree via Udacity</div>
		       	 			<div class="list"> <FaSeedling size="0.7em" color='#689791'/> Building raised beds for our home garden</div>
		       	 			<div class="list"> <FaSeedling size="0.7em" color='#689791'/> Attempting handstands </div>

		       	 			<br />
		       	 			<div class="title"> EXPERIENCE </div>
		       	 			<div>
		       	 				<div class="project-titles"> <a  target="_blank" href="https://github.com/ikorde/emission-absorption-lines" > <FaPlus size="0.7em" color="red" /> </a>
		       	 					<b> SOFTWARE DEVELOPER INTERN</b> @ <b style={{color:"orange"}}> <a target="_blank" href="https://www.ipac.caltech.edu/">Caltech IPAC</a> </b>  
		       	 				</div>
		       	 				<div class="project-desc">Automated methods for finding peaks in spectra. Accurate classifications shown on left. Graph on right shows data
		       	 					extracted from performing tests over thousands of spectra. Such data from this code will aid in drawing conclusions about galaxy formation.  
								</div>
								<div class="images"> 
									<img src={graph_caltech} alt="results graph" width="350px" height="200px" />
									<img src={results_caltech} alt="results graph" width="350px" height="210px"/>
								</div>
								<br />
							</div>
		       	 			<br />
		       	 			<div>
